namespace Sdl.Sdk.SdlxTmTranslationProvider
{
    using System;
    using System.Collections.Generic;
    using System.ComponentModel;
    using System.Data;
    using System.Drawing;
    using System.Linq;
    using System.Text;
    using System.Windows.Forms;

    /// <summary>
    /// The UI for selecting the type of TM
    /// </summary>
    public partial class SdlxTmTranslationProviderUiForm : Form
    {
        /// <summary>
        /// Initializes a new instance of the <see cref="SdlxTmTranslationProviderUiForm"/> class.
        /// </summary>
        public SdlxTmTranslationProviderUiForm()
        {
            InitializeComponent();
            this.DialogResult = DialogResult.Cancel;
        }

        /// <summary>
        /// Handles the Load event of the SdlxTmTranslationProviderUiForm control.
        /// </summary>
        /// <param name="sender">The source of the event.</param>
        /// <param name="e">The <see cref="System.EventArgs"/> instance containing the event data.</param>
        private void SdlxTmTranslationProviderUiForm_Load(object sender, EventArgs e)
        {
            translationMemoryTypesList.SelectedIndex = 0;
        }

        /// <summary>
        /// Handles the Click event of the cancelButton control.
        /// </summary>
        /// <param name="sender">The source of the event.</param>
        /// <param name="e">The <see cref="System.EventArgs"/> instance containing the event data.</param>
        private void cancelButton_Click(object sender, EventArgs e)
        {
            this.DialogResult = DialogResult.Cancel;
            this.Hide();
        }

        /// <summary>
        /// Handles the Click event of the okButton control.
        /// </summary>
        /// <param name="sender">The source of the event.</param>
        /// <param name="e">The <see cref="System.EventArgs"/> instance containing the event data.</param>
        private void okButton_Click(object sender, EventArgs e)
        {
            this.DialogResult = DialogResult.OK;
            this.Hide();
        }

        /// <summary>
        /// Gets the type of the translation memory.
        /// </summary>
        /// <value>The type of the translation memory.</value>
        public string TranslationMemoryType
        {
            get
            {
                string translationMemoryType = "S42TM-STD";

                switch (this.translationMemoryTypesList.SelectedIndex)
                {
                    case 0:
                        translationMemoryType = "S42TM-STD";
                        break;
                    case 1:
                        translationMemoryType = "S42TM-SQLSRVR";
                        break;
                    case 2:
                        translationMemoryType = "S42TM-WEBSERVICE";
                        break;
                }

                return translationMemoryType;
            }
        }
    }
}
